mapper.select:
select com_id from tb_company
where 1=1

    and credit_code=#{credit_code}


    and org_code=#{org_code}


    and business_num=#{business_num}

limit 1

select com_id from tb_company where 1=1
and com_name=#{_parameter}
limit 1

select * from tb_company
where 1=1

    and com_id=#{_parameter}

select pkid from tb_company_qualification
where 1=1 and channel=1

    and cert_no=#{cert_no}


    and com_id=#{com_id}


    and qual_type=#{qual_type}


    and valid_date=#{valid_date}

limit 1

select pkid from tb_person_${table}
where 1=1

    and `name`=#{name}


    and id_card=#{id_card}


    and category=#{category}


    and cert_no=#{cert_no}


    and seal_no=#{seal_no}


    and major=#{major}

limit 1

select pkid from tb_person_change
where 1=1

    and com_name=#{com_name}


    and flag=#{flag}

limit 1

select pro_id from tb_project where 1=1
and project_no=#{project_no}
limit 1

select pkid from tb_person_project
where 1=1

    and pid=#{pid}


    and name=#{name}


    and com_name=#{com_name}


    and role=#{role}


    and remark=#{remark}

limit 1

select pkid from tb_project_company
where 1=1

    and pid=#{pid}


    and innerId=#{innerId}


    and role=#{role}


    and com_name=#{com_name}


    and type=#{type}

limit 1

select pkid from tb_project_zhaotoubiao
where 1=1

    and pro_id=#{pro_id}


    and zhongbiao_code=#{zhongbiao_code}

limit 1

select pkid from tb_project_design
where 1=1

    and pro_id=#{pro_id}


    and check_number=#{check_number}

limit 1

select pkid from tb_project_build
where 1=1

    and pro_id=#{pro_id}


    and build_licence=#{build_licence}

limit 1

select pkid from tb_project_contract
where 1=1

    and pro_id=#{pro_id}


    and record_code=#{record_code}

limit 1

select pkid from tb_project_completion
where 1=1

    and pro_id=#{pro_id}


    and code=#{code}

limit 1
